function number(r,n,e=0){var u=Math.pow(10,e);return 0!=e?Math.floor((Math.random()*(n-r)+r)*u)/u:Math.floor(Math.random()*(n+1-r))+r}function index(r){return number(0,r.length-1)}function value(r){return r[index(r)]}function rgb(){return`rgb(${number(0,255)}, ${number(0,255)}, ${number(0,255)})`}function rgba(){return`rgba(${number(0,255)}, ${number(0,255)}, ${number(0,255)}, ${number(0,1,2)})`}function hex(){return"#"+number(0,255).toString(16)+number(0,255).toString(16)+number(0,255).toString(16)}function emoji(){for(var r="👍",n=[[9757,9757],[9994,9997],[127744,127776],[127780,128255],[128256,128317],[128329,128377],[128378,128378],[128400,128400],[128405,128406],[128512,128722],[129296,129356],[129360,129510]],e=0;e<n.length;e++)for(var u=n[e],t=u[0];t<u[1];t++)r+="|"+String.fromCodePoint(t);return value(r.split("|"))}function dice(n,e){let u=[];for(let r=0;r<e;r++)u.push(number(1,n));return u}module.exports={number:number,index:index,value:value,rgb:rgb,rgba:rgba,hex:hex,emoji:emoji,dice:dice};